mapper.update:
update PROJECT_INFO set name = #{info.name}, subtitle = #{info.subtitle}, home_page = #{info.homePage}, down_page = #{info.downPage}, type = #{info.sourceType, typeHandler=net.hasor.website.datadao.convert.SourceTypeConvert}, language = #{info.language}, license = #{info.license}, futures = #{info.futures, typeHandler=net.hasor.website.datadao.convert.ProjectFuturesConvert}, modify_time = now() where 1 = 1 and id = #{info.id} and owner_id = #{info.ownerID} and owner_type = #{info.ownerType.type}
#{info.futures, typeHandler=net.hasor.website.datadao.convert.ProjectFuturesConvert},
${info.futures}
@{nonull, info.futures , 'and owner_type in (' + sqlArgs(info.userList) + ')' }
and status != -1 limit 1

update PROJECT_INFO
set present        = #{info.present},
    present_format = #{info.contentFormat, typeHandler=net.hasor.website.datadao.convert.ContentFormatConvert},
    modify_time    = now()
where 1 = 1
  and id = #{info.id}
  and owner_id = #{info.ownerID}
  and owner_type = #{info.ownerType.type}
  and status != -1
limit 1
