mapper.select:
SELECT

FROM
blob_resource
WHERE
instance_id = #{instanceId}
AND
owner_type = #{resourceType,javaType=com.clougence.clouddm.console.web.constants.ResourceType}
AND
blob_type = #{fileType,mode=in,javaType=com.clougence.clouddm.console.web.constants.SecurityFileType,typeHandler=}

mapper.delete:
DELETE
FROM blob_resource
WHERE instance_id = #{instanceId}
  AND owner_type = #{resourceType,javaType=com.clougence.clouddm.console.web.constants.ResourceType}
  AND blob_type = #{fileType,javaType=com.clougence.clouddm.console.web.constants.SecurityFileType}
